import React, { useState, useEffect } from "react";

const initialUsers = [
    { username: "nicu", password: "123" },
    { username: "ion", password: "1234" },
    { username: "vasile", password: "12345" },
];

const App = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [users, setUsers] = useState(initialUsers);

    useEffect(() => {
        const storedUsers = localStorage.getItem("users");
        if (storedUsers) {
            setUsers([...users, ...JSON.parse(storedUsers)]);
        }
    }, [users]);

    const handleLogin = (event) => {
        event.preventDefault();
        const foundUser = users.find(
            (user) => user.username === username && user.password === password
        );
        if (foundUser) {
            setIsLoggedIn(true);
            localStorage.setItem("isLoggedIn", "true");
            localStorage.setItem("user", JSON.stringify(foundUser));
            alert("Login success");
        } else {
            alert("Login failed");
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        localStorage.setItem("isLoggedIn", "false");
        alert("Logout success");
    };

    const renderLoginForm = () => {
        return (
            <div style={styles.container}>
                <h1 style={styles.heading}>Login Form</h1>
                <form onSubmit={handleLogin}>
                    <label style={styles.label}>
                        Username:
                        <input
                            type="text"
                            value={username}
                            onChange={(event) => setUsername(event.target.value)}
                            style={styles.input}
                        />
                    </label>
                    <br />
                    <label style={styles.label}>
                        Password:
                        <input
                            type="password"
                            value={password}
                            onChange={(event) => setPassword(event.target.value)}
                            style={styles.input}
                        />
                    </label>
                    <br />
                    <button type="submit" style={styles.button}>
                        Login
                    </button>
                </form>
            </div>
        );
    };

    const renderLogoutButton = () => {
        const storedUser = localStorage.getItem("user");
        const loggedInUser = storedUser ? JSON.parse(storedUser) : null;
        return (
            <div style={styles.container}>
                {loggedInUser && (
                    <p style={styles.loggedIn}>Logged in as: {loggedInUser.username}</p>
                )}
                <button onClick={handleLogout} style={styles.button}>
                    Logout
                </button>
            </div>
        );
    };

    return <div>{isLoggedIn ? renderLogoutButton() : renderLoginForm()}</div>;
};

const styles = {
    container: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        margin: "50px auto",
        padding: "20px",
        border: "2px solid #ccc",
        borderRadius: "10px",
        maxWidth: "400px",
    },
    heading: {
        fontSize: "32px",
        marginBottom: "20px",
    },
    label: {
        display: "flex",
        flexDirection: "column",
        marginBottom: "10px",
    },
    input: {
        padding: "10px",
        borderRadius: "5px",
        border: "1px solid #ccc",

    },
    button: {
        padding: "10px",
        borderRadius: "5px",
        border: "none",
        backgroundColor: "#4CAF50",
        color: "white",
        fontSize: "16px",
        cursor: "pointer",
        marginBottom: "10px",
    },
    loggedIn: {
        fontWeight: "bold",
        marginBottom: "10px",
    },
};

export default App;
