import React, { useState, useEffect } from "react";

const initialUsers = [
    { username: "nicu", password: "123" },
    { username: "ion", password: "1234" },
    { username: "vasile", password: "12345" },
];

const App = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [users, setUsers] = useState(initialUsers);

    useEffect(() => {
        const storedUsers = localStorage.getItem("users");
        if (storedUsers) {
            setUsers([...users, ...JSON.parse(storedUsers)]);
        }
    }, [users]);

    const handleLogin = (event: { preventDefault: () => void }) => {
        event.preventDefault();
        const foundUser = users.find(
            (user) => user.username === username && user.password === password
        );
        if (foundUser) {
            setIsLoggedIn(true);
            localStorage.setItem("isLoggedIn", "true");
            localStorage.setItem("user", JSON.stringify(foundUser)); // Store the user data in localStorage
            alert("Login success");
        } else {
            alert("Login failed");
        }
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        localStorage.setItem("isLoggedIn", "false");
        alert("Logout success");
    };

    const renderLoginForm = () => {
        return (
            <form onSubmit={handleLogin} style={{ backgroundColor: "lightblue", padding: "20px", borderRadius: "10px" }}>
                <label>
                    Username:
                    <input
                        type="text"
                        value={username}
                        onChange={(event) => setUsername(event.target.value)}
                        style={{ marginLeft: "10px", marginBottom: "10px", padding: "5px", borderRadius: "5px", border: "none" }}
                    />
                </label>
                <br />
                <label>
                    Password:
                    <input
                        type="password"
                        value={password}
                        onChange={(event) => setPassword(event.target.value)}
                        style={{ marginLeft: "10px", marginBottom: "10px", padding: "5px", borderRadius: "5px", border: "none" }}
                    />
                </label>
                <br />
                <button type="submit" style={{ backgroundColor: "purple", color: "white", padding: "5px", borderRadius: "5px", border: "none" }}>Login</button>
            </form>
        );
    };

    const renderLogoutButton = () => {
        const storedUser = localStorage.getItem("user");
        const loggedInUser = storedUser ? JSON.parse(storedUser) : null;
        return (
            <div>
                {loggedInUser && <p style={{ color: "green", fontStyle: "italic" }}>Logged in as: {loggedInUser.username}</p>}
                <button onClick={handleLogout} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "5px", border: "none" }}>Logout</button>
            </div>
        );
    };

    return <div>{isLoggedIn ? renderLogoutButton() : renderLoginForm()}</div>;
};

export default App;
